import whichClass from "../../fonction/projetFonction/WhichClass"
import { useRef } from "react"

export default function ParaProject({source,titre,contenu,number}) {
    const paraInvisible = useRef("");
    function onMouseEnterProjet(e){
      e.target.style.transform="scale(1)"
    }
    
    return (
    <div  className={whichClass(number)}>
        <div style={{boxShadow:"   0 40px 60px -6px black"}} className=' relative mb-[35px] w-full'>
          <img style={{boxShadow:"   0 40px 60px -6px black"}}  src={source} alt="" />
          <div ref={paraInvisible} onMouseEnter={(e)=>onMouseEnterProjet(e)} className="absolute top-0 left-0 w-full h-full bg-belge opacity-70 transition-all duration-300 ease-in transform scale-0 rounded-full">

          </div>
        </div>
        <p className=' sup1400:text-[35px] sup968:text-[30px] text-[20px] font-bold opacity-[0.9] mb-[30px] '> {titre} </p>
        <p className='sup1400:text-[25px] sup968:text-[20px] text-[12px] opacity-[0.8] mb-[30px] '>{contenu} </p>
    </div>
  )
}
