import whichClass from "../../fonction/projetFonction/WhichClass"
import { useRef } from "react"

export default function ParaProject({source,titre,contenu,number}) {
    const paraInvisible = useRef("");

    function onMouseEnterProjet(){
      paraInvisible.current.classList.add("animParaProjet");
    }

    function onMouseLeaveProjet(){
      paraInvisible.current.style.transform="scale(0)"
      paraInvisible.current.style.borderRadius="9999%"
    }
    
    return (
    <div  className={whichClass(number)}>
        <div onMouseEnter={()=>onMouseEnterProjet()} onMouseLeave={()=>onMouseLeaveProjet()} style={{boxShadow:"   0 40px 60px -6px black"}} className=' relative mb-[35px] w-full'>
          <img style={{boxShadow:"   0 40px 60px -6px black"}}  src={source} alt="" />
          <div  ref={paraInvisible}  className="absolute top-0 left-0 w-full h-full bg-black opacity-70 transition-all  duration-700 ease-in-out transform scale-0 rounded-[100%] ">

          </div>
        </div>
        <p className=' sup1400:text-[35px] sup968:text-[30px] text-[20px] font-bold opacity-[0.9] mb-[30px] '> {titre} </p>
        <p className='sup1400:text-[25px] sup968:text-[20px] text-[12px] opacity-[0.8] mb-[30px] '>{contenu} </p>
    </div>
  )
}
