const texteUnAng="Hi, I'm Aurélien, i take a lot of pleasure when I code.";

const texteDeuxAng =(
  <>
    I have a particular appetite for 
    <span className='text-jaune font-bold '> Front-end </span> 
    development.
  </>
);

const texteTroisAng="This allows me to express my creativity while regularly facing new experiences."

const texteQuatreAng =(
  <>
   That's why I enjoy replicating website 
   <span className='text-jaune font-bold '> integrations </span>
    that I discover, especially when they feature interesting 
    <span className='text-jaune font-bold'> UI </span>
     or 
     <span className='text-jaune font-bold'> UX </span>.
  </>
);

const texteUnFr ="Salut, je m'appelle Aurélien, je prends énormément de plaisir lorsque je code.";

const texteDeuxFr =(
  <>
    j'ai une appétence particulière pour le développement
    <span className='text-jaune font-bold '> Front-end, </span> 
    .
  </>
);

const texteTroisFr="Cela me permet d'exprimer ma créativité tout en faisant régulièrement face à de nouvelles expériences."

const texteQuatreFr =(
  <>
   C'est pourquoi j'apprécie de reproduire des  
   <span className='text-jaune font-bold '> integrations </span>
    de sites web que je découvre, en particulier lorsqu'ils présentent une 
    <span className='text-jaune font-bold'> UI </span>
     ou une 
     <span className='text-jaune font-bold'> UX </span>
     intéressante.
  </>
);

const tabTexteAbout ={
  fr:[texteUnFr,texteDeuxFr,texteTroisFr,texteQuatreFr],
  ang:[texteUnAng,texteDeuxAng,texteTroisAng,texteQuatreAng]
}

export default tabTexteAbout;
