import React, { useRef,useState,useEffect } from 'react'
import hamburger from '../../Asset/Img/hamburger.png'
import croix from '../../Asset/Img/croixNoir.png'
import github from '../../Asset/Img/github.png'
import mail from '../../Asset/Img/mail.png'
import linkedin from '../../Asset/Img/linkdins.png'
import frFlag from '../../Asset/Img/frenchFlag.png'
import engFLag from '../../Asset/Img/englishFlag.png'
import scrollInToo from '../../fonction/navFonction/ScrollFonction'
import { useContext } from 'react'
import langueContext from '../../context/LangueContext'

export default function MobileNav() {
  const [active, setactive] = useState();
  const x = useRef("");
  const burger = useRef("");
  const nav = useRef("");
  const {langue,setlangue} = useContext(langueContext);

  useEffect(() => {
    if(active===true){
      burger.current.style.transform ="translateX(30px)";
      burger.current.style.opacity ="0";
      x.current.style.display ="block";
      nav.current.style.display ="flex";
      setTimeout(() => {
          x.current.style.transform ="translateX(5px)";
          x.current.style.opacity ="1";
          nav.current.style.transform ="translateX(0px)";
          nav.current.style.opacity ="1";
      }, 10);
      setTimeout(() => {
        burger.current.style.display ="none";
      }, 150);
    }
    if(active===false){
      burger.current.style.display ="block";
      x.current.style.transform ="translateX(30px)";
      x.current.style.opacity ="0";
      nav.current.style.transform ="translateX(80px)";
      nav.current.style.opacity ="0";
       setTimeout(() => {
        burger.current.style.transform ="translateX(0px)";
        burger.current.style.opacity ="1";
      }, 10);
      setTimeout(() => {
         x.current.style.display ="none";
        nav.current.style.display ="none";
      }, 150);
    }
  }, [active,setactive])

  return (
    <nav className='navbar fixed w-full my-0 mx-0 z-50 '>
        <div className='w-[80%] my-0 mx-auto flex items-center justify-end pt-[40px] relative bg-red-400'>
            <img onClick={()=>setactive(true)} ref={burger} className='absolute w-[15px] hover:cursor-pointer top-[100%] transition-all duration-150 ease-in z-50' src={hamburger} alt="" />
            <img onClick={()=>setactive(false)} ref={x} className='absolute w-[20px] hidden hover:cursor-pointer top-[95%]  transform translate-x-[50px] opacity-0 transition-all duration-150 ease-in  z-50' src={croix} alt="" />
            <div ref={nav} className='absolute  h-[220px] w-[80px] top-[100%] hidden flex-col justify-evenly items-center transform translate-x-[80px] opacity-0 transition-all duration-150 ease-in z-40 '>
                <span onClick={()=>scrollInToo(".projet")} className='text-[12px] hover:cursor-pointer' >{langue==="francais"?"Projet":"Project"}</span>
                <span onClick={()=>scrollInToo(".skills")} className='text-[12px] hover:cursor-pointer'>{langue==="francais"?"Compétences":"Skills"}</span>
                <span onClick={()=>scrollInToo(".presention")} className='text-[12px] hover:cursor-pointer'>{langue==="francais"?"Présentation":"About"}</span>
                <p className='w-[100%] flex justify-between'>
                  <img className='w-[19px]' src={linkedin} alt="" />
                  <img className='w-[19px]' src={github} alt="" />
                  <img className='w-[19px]' src={mail} alt="" />
                </p>
                <p className='w-[100%] flex justify-evenly'>
                     <img onClick={()=>setlangue("francais")} className='w-[19px] hover:cursor-pointer' src={frFlag} alt="" />
                    <img onClick={()=>setlangue("anglais")} className='w-[19px] hover:cursor-pointer' src={engFLag} alt="" />
                </p>
            </div>
        </div>
    </nav> 
  )
}
