import whichClass from "../../fonction/projetFonction/WhichClass"
import { useRef } from "react"
import onMouseEnterProjet from "../../fonction/projetFonction/OnMouseEnterProjet";
import onMouseLeaveProjet from "../../fonction/projetFonction/OnMouseLeaveProjet";

export default function ParaProject({source,titre,contenu,number}) {
    const paraInvisible = useRef("");
    return (
    <div  className={whichClass(number)}>
        <div onMouseEnter={()=>onMouseEnterProjet(paraInvisible)} onMouseLeave={()=>onMouseLeaveProjet(paraInvisible)} style={{boxShadow:"   0 40px 60px -6px black"}} className='hover:cursor-pointer relative mb-[80px] w-full '>
          <img style={{boxShadow:"   0 40px 60px -6px black"}}  src={source} alt="" />
          <div  ref={paraInvisible}  className="absolute top-0 left-0 w-full h-full bg-black opacity-30 transform scale-0 rounded-[100%] flex items-center justify-center">
              <button className="text-black">
                  watch
              </button>
          </div>
        </div>
        <p className=' sup1400:text-[35px] sup968:text-[30px] text-[20px] font-bold opacity-[0.9] mb-[30px] '> {titre} </p>
        <p className='sup1400:text-[25px] sup968:text-[20px] text-[12px] opacity-[0.8] mb-[30px] '>{contenu} </p>
    </div>
  )
}
